//#defines
#define SIGN(in) \
   (1|(in>>(sizeof(in)*8-1)))

#define FROM_INT(type,power) \
   type type##_from_int(int in) { return (type)in<<power; }

#define TO_INT(type,power) \
   int type##_to_int(type in) { return (int)(in>>power); }

#define DIV(type,con,power) \
   type type##_div(type a, type b) { return ((con)a*((con)1<<power))/b; }

#define MUL(type,con,power) \
   type type##_mul(type a, type b) { return ((con)a*(con)b)/((con)1<<power); }

#define ROUND(type,power,and) \
   type type##_round(type in) { int sign = SIGN(in); in*=sign; in = ((in+((type)1<<(power-1))))&(and); in*=sign; return in; }

#define FLOOR(type,and) \
   type type##_floor(type in) { int sign = SIGN(in); in*=sign; in&=(and); in*=sign; return in; }

#define CEIL(type,power,and) \
   type type##_ceil(type in) { int sign = SIGN(in); in*=sign; in = (in+((type)1<<power)-1)&(and); in*=sign; return in; }

#define SIN(type,shift) \
   type type##_sin(type in) { return type##_sin_lt[(in>>shift)&1023]; }

#define COS(type,shift) \
   type type##_cos(type in) { return type##_sin_lt[((in>>shift)+256)&1023]; }

#define SQRT(type,con,shift) \
   type type##_sqrt(type in) { int sign = SIGN(in); in*=sign; con result = 0, a = (con)in<<(shift/2), b = (con)1L<<(shift*2-2); while(b>a) b>>=2; \
   while(b) { if(a>=result+b) { a-=result+b; result = result+2*b; } b>>=2; result>>=1; } return result*sign; }

#define DEG2ANGLE(type) \
   type type##_deg_to_angle(type in) { return type##_div(type##_mul(type##_from_int(8),in),type##_from_int(360)); }

#define ANGLE2DEG(type) \
   type type##_angle_to_deg(type in) { return type##_mul(type##_from_int(45),in); } 

#define RAD2ANGLE(type,pi) \
   type type##_rad_to_angle(type in) { return type##_div(type##_mul(type##_from_int(8),in),2*pi); }

#define ANGLE2RAD(type,pi) \
   type type##_angle_to_rad(type in) { return type##_div(type##_mul(pi,in),type##_from_int(4)); }

#define VADD(vtype,dim) \
   void vtype##_add(vtype out, const vtype a, const vtype b) { for(int i = 0;i<dim;i++) out[i] = a[i] + b[i]; }

#define VCROSS(type,vtype) \
   void vtype##_cross(vtype out, const vtype a, const vtype b) { type a0 = a[0],a1 = a[1],a2 = a[2],b0 = b[0], b1 = b[1], b2 = b[2]; \
      out[0] = type##_mul(a1,b2)-type##_mul(a2,b1); out[1] = type##_mul(a2,b0)-type##_mul(a0,b2); out[2] = type##_mul(a0,b1)-type##_mul(a1,b0); }

#define VDIST(type,vtype,dim) \
   type vtype##_dist(const vtype a, const vtype b) { type out = 0; for(int i = 0;i<dim;i++) out+=type##_mul(b[i]-a[i],b[i]-a[i]); return type##_sqrt(out); }

#define VDIST2(type,vtype,dim) \
   type vtype##_dist2(const vtype a, const vtype b) { type out = 0; for(int i = 0;i<dim;i++) out+=type##_mul(b[i]-a[i],b[i]-a[i]); return out; }

#define VDIV(type,vtype,dim) \
   void vtype##_div(vtype out, const vtype in, type s) { for(int i = 0;i<dim;i++) out[i] = type##_div(in[i],s); }

#define VDOT(type,vtype,dim) \
   type vtype##_dot(const vtype a, const vtype b) { type out = 0; for(int i = 0;i<dim;i++) out+=type##_mul(a[i],b[i]); return out; }

#define VLERP(type,vtype,dim) \
   void vtype##_lerp(vtype out, const vtype a, const vtype b, type t) { for(int i = 0;i<dim;i++) out[i] = a[i]+type##_mul(t,b[i]-a[i]); }

#define VMAG(type,vtype,dim) \
   type vtype##_mag(const vtype in) { type out = 0; for(int i = 0;i<dim;i++) out+=type##_mul(in[i],in[i]); return type##_sqrt(out); } 

#define VMAG2(type,vtype,dim) \
   type vtype##_mag2(const vtype in) { type out = 0; for(int i = 0;i<dim;i++) out+=type##_mul(in[i],in[i]); return out; } 

#define VMUL(type,vtype,dim) \
   void vtype##_mul(vtype out, const vtype in, type s) { for(int i = 0;i<dim;i++) out[i] = type##_mul(in[i],s); }

#define VNORM(type,vtype,dim) \
   void vtype##_norm(vtype out, const vtype in) { type mag = vtype##_mag(in); if(mag!=0) for(int i = 0;i<dim;i++) out[i] = type##_div(in[i],mag); }

#define VSUB(vtype,dim) \
   void vtype##_sub(vtype out, const vtype a, const vtype b) { for(int i = 0;i<dim;i++) out[i] = a[i]-b[i]; }

#define MADD(mtype, dim) \
   void mtype##_add(mtype out, const mtype a, const mtype b) { for(int i = 0;i<dim*dim;i++) out[i] = a[i]+b[i]; }

#define MCOPY(mtype,dim) \
   void mtype##_copy(mtype dst, const mtype src) { for(int i = 0;i<dim*dim;i++) dst[i] = src[i]; }

#define MDET2(mtype,type) \
   type mtype##_determinant(const mtype in) { return type##_mul(in[0],in[3])-type##_mul(in[1],in[2]); }

#define MDET3(mtype,type) \
   type mtype##_determinant(const mtype in) { return type##_mul(in[0],type##_mul(in[4],in[8])-type##_mul(in[5],in[7])) \
                                                    -type##_mul(in[1],type##_mul(in[3],in[8])-type##_mul(in[5],in[6])) \
                                                    +type##_mul(in[2],type##_mul(in[3],in[7])-type##_mul(in[4],in[6]));  }

#define MIDENT(mtype,type,dim) \
   void mtype##_identity(mtype out) { for(int i = 0;i<dim*dim;i++) out[i] = 0; for(int i = 0;i<dim;i++) out[i*dim+i] = type##_from_int(1); }

#define MSUB(mtype,dim) \
   void mtype##_sub(mtype out, const mtype a, const mtype b) { for(int i = 0;i<dim*dim;i++) out[i] = a[i]-b[i]; }
